import React, { useState } from 'react';
import './App.css';

class Forms extends Component {
  constructor(props) {
      super(props)
      this.state = {
          Company: null
      }
  }

  handleSubmit = (event) => {
      event.preventDefault()
  }

  handleInputChance = (event) => {
      event.preventDefault()
      console.log(event)
      console.log(event.target.name)
      console.log(event.target.value)
      this.setState({
          [event.target.name]: event.target.value
      })
  }

  render() {
      const {Company} = this.state
      return (
          <div>
              <p>Company: {Company}</p>
              <div className="form-container">
      <form className="register-form" onSubmit={handleSubmit}>
        {submitted && valid ? <div className="success-message">Success!</div>: null}
        <input
          onChange={handleCompanyInputChange}
          value={values.Company}
          className="form-field"
          placeholder="Company Name"
          name="Company"/>
          {submitted && !values.Company ? <span>Please enter a company name</span>: null}
          <button class="form-field" type="submit">
          Submit
        </button>
      </form>
    </div>
          </div>
      )
  }
}
export default Forms;


function App() {
  const[values, setValues] = useState({
    Company: "",
  });
  const [submitted, setSubmitted] = useState(false);
  const [valid, setValid] = useState(false);

  const handleCompanyInputChange = (event) => {
    setValues({...values, Company: event.target.value})
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    if (values.Company) {
      setValid(true);
    }
    setSubmitted(true);
  }

  return (
    <div>
      <h1>Slogify</h1>

    <h2>Advertising Made Easy</h2>
    </div>

  );
}
