import React, { useState } from 'react';
import './App.css';

function App() {
  const[values, setValues] = useState({
    Company: "",
    Description: "",
  });
  const [submitted, setSubmitted] = useState(false);
  const [valid, setValid] = useState(false);

  const handleCompanyInputChange = (event) => {
    setValues({...values, Company: event.target.value})
  }

  const handleDescriptionInputChange = (event) => {
    setValues({...values, Description: event.target.value})
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    if (values.Company && values.Description) {
      setValid(true);
    }
    setSubmitted(true);
  }

  return (
    <div>
      <h1>Slogify</h1>
      <h2>Advertising Made Easy</h2>
    <div className="form-container">
      <form className="register-form" onSubmit={handleSubmit}>
        {submitted && valid ? <div className="success-message">Success!</div>: null}
        <input
          onChange={handleCompanyInputChange}
          value={values.Company}
          className="form-field"
          placeholder="Company Name"
          name="Company"/>
          {submitted && !values.Company ? <span>Please enter a company name</span>: null}
          <input
          onChange={handleDescriptionInputChange}
          value={values.Description}
          className="form-field"
          placeholder="Description"
          name="Description"/>
          {submitted && !values.Description ? <span>Please enter a description</span>: null}
          <button class="form-field" type="submit">
          Submit
        </button>
      </form>
    </div>
    </div>
  );
}

export default App;
